#include<iostream>
#include<string>
#include<unordered_set>
#include<chrono>
#include<string_view>
using namespace std::chrono;
using namespace std;

class Solution {
public:
    string findOrNot(int i, string S, unordered_set<string_view> &hash)
    {
        
        for (int j = 0; j <= S.size() - i; j++)
        {
            const auto [it, inserted] = hash.emplace(S.data() + j, i);
            if (!inserted)
            {
                return {it->begin(), it->end()};
            }
        }
        return "";
    }
    string longestDupSubstring(string S) {
        int l = 0;
        int r= S.size() - 1;
        int maxSubstrLength = 0;
        string substrng;
        unordered_set<string_view> hash;
        //pair<int, string> maxPair = make_pair(0,"");
        while (l <= r)
        {
            int m = l + (r - l) / 2;
             string str = findOrNot(m, S, hash);

            // If x greater, ignore left half 
            if (str != "")
            {
                if (substrng.length() < str.length())
                {
                    substrng = str;
                }
                l = m + 1;
            }
            else
            {
                r = m - 1;
            }
            hash.clear();
        }
        return substrng;
    }
};
